package com.example.new_androidclient.hazard.bean;import androidx.databinding.BaseObservable;import androidx.databinding.Bindable;import com.example.new_androidclient.BR;public class HazardDetailBean extends BaseObservable {    /**     * id : 258     * hazardContent : null     * hazardName : null     * investigationResut : 1     * investigationContent : 企业应每年确认操作规程与工艺卡片的适应性和有效性；     * limitRectifyTime : null     * investigationUser : null     */    private int id;    private String hazardContent;    private String hazardName;    private String investigationResut;    private String investigationContent;    private String limitRectifyTime;    private String investigationUser;    @Bindable    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    @Bindable    public String getHazardContent() {        return hazardContent;    }    public void setHazardContent(String hazardContent) {        this.hazardContent = hazardContent;    }    @Bindable    public String getHazardName() {        return hazardName;    }    public void setHazardName(String hazardName) {        this.hazardName = hazardName;    }    @Bindable    public String getInvestigationResut() {        return investigationResut;    }    public void setInvestigationResut(String investigationResut) {        this.investigationResut = investigationResut;    }    @Bindable    public String getInvestigationContent() {        return investigationContent;    }    public void setInvestigationContent(String investigationContent) {        this.investigationContent = investigationContent;    }    @Bindable    public String getLimitRectifyTime() {        return limitRectifyTime;    }    public void setLimitRectifyTime(String limitRectifyTime) {        this.limitRectifyTime = limitRectifyTime;        notifyPropertyChanged(BR.limitRectifyTime);    }    @Bindable    public String getInvestigationUser() {        return investigationUser;    }    public void setInvestigationUser(String investigationUser) {        this.investigationUser = investigationUser;    }    public void updateData(){        notifyChange();    }}